mapper.select:
SELECT a.id,a.`title`,a.`edit_time`,a.`page_view`,a.`state`,u.`nickname`,c.`cate_name`,a.uid FROM article a,user
u,category c WHERE a.`cid`=c.`id` AND a.`uid`=u.`id`

    and a.uid=#{uid}


    and a.state=#{state}


    and a.state=1


    AND title LIKE concat('%',#{keywords},'%')

ORDER BY a.edit_time DESC limit #{start},#{count};

SELECT a.id,a.`title`,a.`edit_time`,a.`page_view`,a.`state`,u.`nickname`,c.`cate_name`,a.uid FROM article a,user u,category c WHERE a.`cid`=c.`id` AND a.`uid`=u.`id` and a.state=1
AND title LIKE concat('%',#{keywords},'%')
ORDER BY a.edit_time DESC limit #{start},#{count};

SELECT count(*) FROM article


        AND state=#{state}


        AND uid=#{uid}


        AND title LIKE concat('%',#{keywords},'%')
